mapper.select:
SELECT *
FROM schedules
WHERE conId = #{param1} AND DATE_FORMAT(conDate, '%Y-%m-%d') = #{param2}

insert.foreach:
INSERT INTO booked (sId, seatNum)
SELECT id, #{seatNum}
FROM schedules
WHERE conId = #{conId} AND DATE_FORMAT(schedules.conDate, '%Y-%m-%d') = #{conDate}

mapper.update:
UPDATE small_concert
SET viewCount = viewCount + 1
WHERE id = #{param1}

UPDATE busking
SET viewCount = viewCount + 1
WHERE id = #{param1}

UPDATE local_festival
SET viewCount = viewCount + 1
WHERE id = #{param1}
